te: function() {                        $(".edit_form input, .edit_form button").prop("disabled", false);                    }                });            });            $(document).on("click", "#create_modal_btn", function(e) {                $.ajax({                    type: "POST",                    url: "{{route('Backend.categories.create')}}",                    data: $(".create_form").serialize(),                    dataType: "json",                    beforeSend: function() {                        $(".create_form input, .create_form button").prop("disabled", true);                    },                    success: function(e) {                        console.log(e);                        $("tbody").prepend(e.htmlElement);                        $("#new_category").modal("hide");                    },                    error: function(x) {                        var errorResponse = x.responseJSON || x.responseText;                        console.error(errorResponse);                        if(errorResponse.type === 'validation_error')                        {                            $.each(Object.entries(errorResponse.message), function (index, value) {                                $(".create_form ."+value[0]+"-error").html("<li>" + value[1] + "</li>");                            });                        }                    },                    complete: function() {                        $(".create_form input, .create_form button").prop("disabled", false);                        e.preventDefault();                    }                });            });        });    </script>@endsection